import { clearCart } from '../features/cart/cartSlice'
import { closeModal } from '../features/modal/modalSlice'
import React, { useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { calculateTotals } from '../features/cart/cartSlice';
import { AppDispatch, RootState } from '../store'; // 自分のプロジェクトのパスに置き換えてください
import AddModal from '../components/AddModal';
import Modal from '../components/Modal';
import CartContainer from './CartContainer';



const CartModal: React.FC = () => {
  const dispatch = useDispatch()
  const { cartItems } = useSelector((state: RootState) => state.cart);
  const { isOpen, isAddOpen } = useSelector((state: RootState) => state.modal);

  useEffect(() => {
    dispatch(calculateTotals());
  }, [cartItems, dispatch]);

  return (
    <div className="modal add">
            <h4>test</h4>
            <div className="btn-container">
                <button className="btn confirm-btn" onClick={() => {
                    dispatch(clearCart());
                    dispatch(closeModal())
                    }}>OK</button>
                <button className="btn clear-btn" onClick={() => dispatch(closeModal())}>やめとく</button>
            </div>
        </div>

  );
};

export default CartModal
