import { FC } from "react";
import { useForm, SubmitHandler} from "react-hook-form";
import "./styles.css";
import 'bootstrap/dist/css/bootstrap.min.css';
import Button from 'react-bootstrap/Button';
import axios from 'axios';


 interface Toroku {
  email: string;
  password: string;
}

const Toroku: FC = () => {
  const {
    register,
    handleSubmit,
    watch,
    formState: { 
      errors,
      isValid,
      isSubmitting
     },
  } = useForm<Toroku>();
  const onSubmit: SubmitHandler<Toroku> = async (data: Toroku) => {
    console.log('Sending data:', data);

  try {
  await axios.post('http://localhost:8080/api/v1/new', data, {
  headers: {
    'Content-Type': 'application/json'
  }
});
//console.log('Success:', response.data);
}catch(error){
  if (axios.isAxiosError(error) && error.response) {
    console.log('Error response data:', error.response.data);
    console.log('Error response status:', error.response.status);
    console.log('Error response headers:', error.response.headers);
  } else if (axios.isAxiosError(error) && error.request) {
    console.log('Error request:', error.request);
  } else {
    console.log('Error message:', (axios.isAxiosError(error) && error.message));
  }
  console.log('Error config:', (axios.isAxiosError(error) && error.config));
}
};



  return (
    <div className="wrapper">
      <h1>新規会員登録</h1>
      <section className="section">
        {/* <h2>useState Form</h2> */}
        {/* <p>React Hook Formを使用してformを作成した例です。</p> */}
        <form onSubmit={handleSubmit(isValid)}>
          <div className="form-item">
            <label>
              <span className="label required">必須</span>
              <span>Email</span>
              <input
                type="email"
                {...register("email", {
                  required: "メールアドレスを入力してください",
                })}
              />
            </label>
            {errors.email?.message && (
              <p className="error-message">{errors.email?.message}</p>
            )}
          </div>
          <div className="form-item">
            <label>
              <span className="label required">必須</span>
              <span>PASSWORD</span>
              <input
                type="password"
                {...register("password", {
                  required: "パスワードを入力してください",
                })}
              />
            </label>
            {errors.password?.message && (
              <p className="error-message">{errors.password?.message}</p>
            )}
          </div>
          {/* <div className="form-item">
            <label>
              <span className="label required">必須</span>
              <span>コメント</span>
              <textarea
                {...register("comment", {
                  required: true,
                  minLength: {
                    value: 10,
                    message: "10文字以上で入力してください",
                  },
                  maxLength: {
                    value: 20,
                    message: "20文字以下で入力してください",
                  },
                })}
              />
            </label>
            {errors.comment?.message && (
              <p className="error-message">{errors.comment.message}</p>
            )}
          </div> */}
          <div className="submit-button">
          <Button type="submit">登録する</Button>
          </div>
        </form>
      </section>
      {/* <p>姓: {lastName}</p> */}
    </div>
  );
};


export default Toroku;
